import unittest


def which_floor(str_input):
    if not isinstance(str_input, str):
        raise TypeError(
            f"Error: Expected input to be of type 'str', received {type(str_input)}.\nTry again. Terminating...")
    floor = 0
    for char in str_input:
        if char == "(":
            floor += 1
        if char == ")":
            floor -= 1
    return floor


