class TestWhichFloor(unittest.TestCase):
    """Testing the which_floor function"""

    def testing_which_floor(self):
        """Testing the which_floor function"""
        test_input = "(())"
        self.assertEqual(which_floor(test_input), 0)
        test_input = "()()"
        self.assertEqual(which_floor(test_input), 0)
        test_input = "((("
        self.assertEqual(which_floor(test_input), 3)
        test_input = "(()(()("
        self.assertEqual(which_floor(test_input), 3)
        test_input = "))((((("
        self.assertEqual(which_floor(test_input), 3)
        test_input = "())"
        self.assertEqual(which_floor(test_input), -1)
        test_input = "))("
        self.assertEqual(which_floor(test_input), -1)
        test_input = ")))"
        self.assertEqual(which_floor(test_input), -3)
        test_input = ")())())"
        self.assertEqual(which_floor(test_input), -3)
        test_input = santa_string
        self.assertEqual(which_floor(test_input), 280)


